mapper.select:
SELECT NO, TITLE, CONTENT, ID, HIT, REGDATE FROM ( SELECT /*+ INDEX_DESC(BL_BOARD PK_BOARD) */ ROWNUM RN, NO, TITLE, CONTENT, ID, HIT, REGDATE FROM TBL_BOARD WHERE 1=1
AND (TITLE LIKE '%'||#{searchText}||'%' OR CONTENT LIKE '%'||#{searchText}||'%')
AND ROWNUM <= #{pageNum} * #{amount} ) WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE SIMPLE_BOARD SET
	TITLE=#{title}, 
	CONTENT=#{content} 
WHERE NO=#{no}

UPDATE SIMPLE_BOARD SET
	HIT=HIT+1
WHERE NO=#{no}
