mapper.select:
SELECT A.EMPLOYEE_ID, A.FIRST_NAME, A.LAST_NAME, A.EMAIL, A.PHONE_NUMBER, A.HIRE_DATE, A.SALARY, A.JOB_ID, A.JOB_TITLE, A.DEPARTMENT_ID, A.DEPARTMENT_NAME, A.LOCATION_ID, A.STATE_PROVINCE, A.CITY, A.COUNTRY_ID, A.COUNTRY_NAME FROM( SELECT /*+ INDEX_DESC(EMPLOYEES EMP_EMP_ID_PK) */ ROWNUM RN, E.EMPLOYEE_ID, E.FIRST_NAME, E.LAST_NAME, E.EMAIL, E.PHONE_NUMBER, E.HIRE_DATE, E.SALARY, E.JOB_ID, J.JOB_TITLE, E.DEPARTMENT_ID, D.DEPARTMENT_NAME, D.LOCATION_ID, L.STATE_PROVINCE, L.CITY, L.COUNTRY_ID, C.COUNTRY_NAME FROM EMPLOYEES E LEFT OUTER JOIN JOBS J ON E.JOB_ID = J.JOB_ID LEFT OUTER JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID LEFT OUTER JOIN LOCATIONS L ON D.LOCATION_ID = L.LOCATION_ID LEFT OUTER JOIN COUNTRIES C ON L.COUNTRY_ID = C.COUNTRY_ID WHERE 1=1
AND (E.FIRST_NAME LIKE '%'||#{searchText}||'%' OR E.LAST_NAME LIKE '%'||#{searchText}||'%')
AND ROWNUM <= #{pageNum} * #{amount} )A WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.insert:

        INSERT INTO EMPLOYEES(
        	EMPLOYEE_ID,
        	FIRST_NAME,
        	LAST_NAME,
        	EMAIL,
        	PHONE_NUMBER,
        	HIRE_DATE,
        	JOB_ID,
        	SALARY,
        	COMMISSION_PCT,
        	MANAGER_ID,
        	DEPARTMENT_ID) 
		VALUES (
			EMPLOYEES_SEQ.NEXTVAL		
			, #{firstName, jdbcType=VARCHAR}
			, #{lastName, jdbcType=VARCHAR}
			, #{email, jdbcType=VARCHAR}
			, #{phoneNumber, jdbcType=VARCHAR}
			, to_date(#{hireDate}, 'YYYY-MM-DD')
			, #{jobId, jdbcType=VARCHAR}
			, #{salary, jdbcType=NUMERIC }
			, #{commissionPct, jdbcType=NUMERIC }
			, #{managerId, jdbcType=NUMERIC}
			, #{departmentId, jdbcType=NUMERIC}
		)
